import React, { useState } from 'react';
import { Button } from "rbx";
import UsersService from '../../../services/users';
import { Redirect } from "react-router-dom";

// When the account is deleted, redirect the user to Home.
function UsersDelete() {
    const [redirectToHome, setRedirectToHome] = useState(false);

    // Replace 'userId' with the actual user ID or identifier
    const userId = '123'; // Provide the appropriate user ID

    // window.confirm - For the user to confirm that they really want to execute the command.
    const deleteUser = async () => {
        if (window.confirm('Are you sure you want to delete this account?')) {
            try {
                await UsersService.delete(userId);
                setRedirectToHome(true);
            } catch (error) {
                console.error("Error deleting user:", error);
            }
        }
    }

    if (redirectToHome) {
        return <Redirect to={{ pathname: "/" }} />
    }

    return (
        <Button color="danger" onClick={deleteUser}>
            Delete account
        </Button>
    )
}

export default UsersDelete;
