import React, { Fragment, useState, useEffect } from 'react';

import ReactQuill from 'react-quill'; // ES6
import 'react-quill/dist/quill.snow.css'; // Quill visual theme

function Editor(props) {
    const [currentContent, setCurrentContent] = useState('')


    


    // Generating the title through the body. 
    // It will show the first 30 characters of the note as the title.
    const updateNote = (content) => {
        const title = content.replace(/(<([^>]+)>)/ig, "").slice(0, 30);
        props.updateNote(props.note, {'title': title, 'body': content})
    }

    useEffect(()=> {
        setCurrentContent(props.note.body)
    }, [props.note])

    // Editing options available for user to create their note.
    const modules = {
        toolbar: [
            [{ 'header': '1'}, {'header': '2'}, { 'font': [] }],
            ['bold', 'italic', 'underline', 'strike', 'blockquote', 'code-block'],
            [{'list': 'ordered'}, {'list': 'bullet'},
            {'indent': '-1'}, {'indent': '+1'}],
            ['link'],
            ['clean'],
        ]
    }

    return (
        <Fragment>
            <ReactQuill value={currentContent} modules={modules}/>
        </Fragment>
    )
}

export default Editor;
